//multidim
class arraymultidi
{
 public static void main(String arg[])
 {
  int arraymultidi[][]={{1,2},{1,2,4},{5,6,7,8}};
  for(int row[]:arraymultidi)
  {
   for(int col:row)
   {
    System.out.print(col);
     System.out.print("\t");
   }
    System.out.println();
  }
 }
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
